import { Forms } from "./forms.v2.min.js";
import { Network } from "./network.v2.min.js";
export const NetworkHandler = { setupform: (form, endpoint, success, error) => { Forms.setup({ element: form, endpoint: endpoint, json: !0 }, success, error) }, getrequest: async endpoint => { let response = await Network.sendGET({ endpoint: endpoint, json: !0 }); return NetworkHandler.validaterequest(response) }, postrequest: async(endpoint, data) => { let response = await Network.sendPOST({ endpoint: endpoint, data: data, json: !0 }); return NetworkHandler.validaterequest(response) }, validaterequest: response => { if ("success" != response.status) throw Error(response.message); return response } };